import styles from './Product.module.scss';
import clsx from 'clsx';
import Button from '../Button/Button';
import ProductImage from '../ProductImage/ProductImage';
import { useState } from 'react';

const Product = props => {

  const [currentSize, setCurrentSize] = useState(props.sizes[0].name);
  const [currentColor, setCurrentColor] = useState(props.colors[0]);
  const [currentPrice, setCurrentPrice] = useState(props.basePrice);

  const prepareColorClassName = color => {
    return styles['color' + color[0].toUpperCase() + color.substr(1).toLowerCase()];
  }

  const prepareSizeClassNameActive = size => {
    if (size === currentSize){
      return styles.active;
    }
    else {return ''};
  }

  const prepareColorClassNameActive = color => {
    if (color === currentColor){
      return styles.active;
    }
    else {return ''};
  }

  const getPrice = additionalPrice => {
    console.log(additionalPrice);
    setCurrentPrice(props.basePrice + additionalPrice);
  }

  return (
    <article className={styles.product}>
      <ProductImage title={props.title} name={props.name} currentColor={currentColor}/>
      <div>
        <header>
          <h2 className={styles.name}>{props.title}</h2>
          <span className={styles.price}>Price: {currentPrice}</span>
        </header>
        <form>
          <div className={styles.sizes}>
            <h3 className={styles.optionLabel}>Sizes</h3>
            <ul className={styles.choices}>
              {props.sizes.map(size => <li key={size.name}><button type="button" onClick={() => {setCurrentSize(size.name); getPrice(size.additionalPrice);}} className={clsx(prepareSizeClassNameActive(size.name), size.name === size.name,  )}>{size.name}</button></li>)}
            </ul>
          </div>
          <div className={styles.colors}>
            <h3 className={styles.optionLabel}>Colors</h3>
            <ul className={styles.choices}>
              {props.colors.map(color => <li key={color}><button type="button" onClick={() => {setCurrentColor(color);}} className={clsx(prepareColorClassName(color), color === color, prepareColorClassNameActive(color), color === color)} /></li>)}
            </ul>
          </div>
          <Button className={styles.button}>
            <span className="fa fa-shopping-cart" />
          </Button>
        </form>
      </div>
    </article>
  )
};

export default Product;
